// Use different placeholder image services for variety
const getUniqueImageSource = (itemId: string, seed = 0) => {
  // Use different placeholder services based on the item type
  const services = [
    // Placeholder.com - colored backgrounds with text
    (id: string) => `https://via.placeholder.com/400x300/${getColorCode(id)}/FFFFFF?text=${encodeURIComponent(id)}`,

    // PlaceIMG - different categories
    (id: string) => `https://placeimg.com/400/300/${getPlaceImgCategory(seed)}`,

    // LoremFlickr - specific categories
    (id: string) => `https://loremflickr.com/400/300/${getFlickrCategory(id)}`,

    // Picsum - random beautiful images with seed
    (id: string) => `https://picsum.photos/seed/${id}/400/300`,

    // Our provided image with different crop parameters
    (id: string) =>
      `https://hebbkx1anhila5yf.public.blob.vercel-storage.com/ScreenShot-2024-10-23_0-21-9.png-zdtdPbZuXhrgva9rrjri8YDxK4Qkod.jpeg?item=${id}&crop=${seed}`,
  ]

  // Get a deterministic service based on the item ID
  const serviceIndex = getHashFromString(itemId) % services.length
  return services[serviceIndex](itemId)
}

// Helper functions to create variety
const getColorCode = (str: string) => {
  let hash = 0
  for (let i = 0; i < str.length; i++) {
    hash = str.charCodeAt(i) + ((hash << 5) - hash)
  }
  const color = Math.abs(hash).toString(16).substring(0, 6).padStart(6, "0")
  return color
}

const getPlaceImgCategory = (seed: number) => {
  const categories = ["animals", "arch", "nature", "people", "tech"]
  return categories[seed % categories.length]
}

const getFlickrCategory = (str: string) => {
  const categories = ["food", "dinner", "lunch", "breakfast", "meal", "restaurant", "plate", "cuisine"]
  return categories[getHashFromString(str) % categories.length]
}

const getHashFromString = (str: string) => {
  let hash = 0
  for (let i = 0; i < str.length; i++) {
    hash = str.charCodeAt(i) + ((hash << 5) - hash)
  }
  return Math.abs(hash)
}

